const callInWindow = require('callInWindow');
const log = require('logToConsole');
const copyFromWindow = require('copyFromWindow');
const createQueue = require('createQueue');
const Math = require('Math');
const callLater = require('callLater');



/***
 * HELPER FUNCTIONS
 */
const dlp = createQueue('dataLayer');
function dataLayerPush(payload) {
  dlp({ ecommerce: null });
  dlp(payload);
  debugLog('pushing to data layer', payload);
}

function assign() {
  const objects = arguments;
  const len = objects.length;
  const newObject = {};
  for (let i = 0; i < len; i++) {
    for (let key in objects[i]) {
      newObject[key] = objects[i][key];
    }
  }
  return newObject;
}

function keys(obj) {
  const keys = [];
  for (let key in obj) {
    keys.push(key);
  }
  return keys;
}

function roundTo(num, len) {
  len = len || 0;
  len = (Math.floor(num) + '').length + len + 1; // account for the length of the number and the decimal
  return ((num * 1.00000000000000000000001) + '').slice(0, len) * 1;
}

function debugLog(msg, obj) {
  log('Olo Serve GTM: ' + msg);
  if (obj) {
    log(obj);
  }
}

function oloOn(configName, cb) {
  // If this event is enabled, listen for it
  if (data[configName]) {
    callInWindow('Olo.on', configName, cb);
    debugLog('listening for "' + configName + '" events.');
  }
  else {
    debugLog('NOT listening for "' + configName + '" events.');
  }
}

function isGa4() {
  return data.compat === 'ga4';
}

function isLegacy() {
  return data.compat === 'legacy';
}

function mapProduct(product) {
  return {
    item_name: product.name,
    item_id: product.id,
    price: product.baseCost || 0.0,
    item_category: product.category.name
  };
}

function mapProductUA(product) {
  return {
    name: product.name,
    id: product.id,
    price: product.baseCost || 0.0,
    category: product.category.name
  };
}

function mapBasketProduct(bp) {
  return {
    item_name: bp.productName,
    item_id: bp.product.id,
    price: bp.unitCost || 0.0,
    item_category: bp.categoryName,
    quantity: bp.quantity
  };
}

function mapBasketProductUA(bp) {
  return {
    name: bp.productName,
    id: bp.product.id,
    price: bp.unitCost || 0.0,
    category: bp.categoryName,
    quantity: bp.quantity
  };
}

function mapBasketProductLegacy(bp) {
  return {
    sku: bp.product.id,
    name: bp.productName,
    category: bp.categoryName,
    price: bp.unitCost || 0.0,
    quantity: bp.quantity
  };
}

function combineMatchingBasketProductsForEcommerce(basketProducts, key, costKey) {
  key = key || 'id';
  costKey = costKey || 'price';
  const combinedBasketProducts = basketProducts.reduce(function(acc, bp) {
    if (!bp) return acc;
    const cur = acc[bp[key]];
    if (!cur) { // No basket product with this ID, store it an continue
      acc[bp[key]] = bp;
      return acc;
    }

    // Combine this basket product with the last one
    // `price` is a per-unit price, and because different instances of the same ID
    // can have different prices (due to modifiers being added), we do our best
    // and find an average price per unit so that the Google Analytics revenue is correct.
    // Calculate the new average price by taking the current price and quantity for this item,
    // multiplying it out to the total cost, adding the total cost of the new item, and averaging
    // both the existing total cost and the new total cost by the new quantity and rounding to two decimal places.
    const newQuantity = cur.quantity + bp.quantity;
    const newAveragePrice =
      Math.round(100 * ((cur[costKey] * cur.quantity + bp[costKey] * bp.quantity) / newQuantity)) / 100;
    acc[bp[key]] = assign(
      cur,
      {
        price: newAveragePrice,
        quantity: newQuantity
      }
    );

    return acc;
  }, {});

  return keys(combinedBasketProducts).map(function(k) {
    return assign(
      combinedBasketProducts[k],
      {
        price: roundTo(combinedBasketProducts[k].price, 2)
      }
    );
  });
}

function getVendor() {
  return copyFromWindow('Olo.data.vendor') || {};
}

function mapAddToCartToDataLayer(product) {
  return isGa4() ? {
    event: 'add_to_cart',
    ecommerce: {
      items: [mapBasketProduct(product)]
    }
  } : {
    event: 'addToCart',
    ecommerce: {
      currencyCode: getVendor().currency,
      add: {
        products: [mapBasketProductUA(product)]
      }
    }
  };
}

function mapRemoveFromCartToDataLayer(product) {
  return isGa4() ? {
    event: 'remove_from_cart',
    ecommerce: {
      items: [mapBasketProduct(product)]
    }
  } : {
    event: 'removeFromCart',
    ecommerce: {
      currencyCode: getVendor().currency,
      remove: {
        products: [mapBasketProductUA(product)]
      }
    }
  };
}

function mapCheckoutToDataLayer(basket, cb) {
  if (isGa4()) {
    // Serve exposes a "done" callback because UA depends on it
    // The idea is that the page shouldn't transition until the analytics
    // are done running. You can see below how UA consumes this.
    // GA4 doesn't have an `eventCallback` parameter, but we want
    // to let Serve know we are done doing our tracking and it can
    // move along ASAP. `callLater` is the same as `setTimeout(0)`.
    callLater(cb);
    return {
      event: 'begin_checkout',
      ecommerce: {
        items: basket.basketProducts.map(mapBasketProduct)
      }
    };
  }
  return {
    event: 'checkout',
    ecommerce: {
      checkout: {
        actionField: { step: 0 },
        products: basket.basketProducts.map(mapBasketProductUA)
      },
      eventCallback: cb || function() {}
    }
  };
}

function mapTransactionToDataLayer(order, orderSubmission) {
  return isGa4() ? {
    event: 'purchase',
    ecommerce: {
      transaction_id: order.displayId,
      affiliation: order.vendorName,
      value: order.subTotal,
      tax: order.vendorTax,
      shipping: order.deliveryCharge,
      currency: getVendor().currency,
      coupon: orderSubmission.basket.coupon ? orderSubmission.basket.coupon.code : '',
      items: combineMatchingBasketProductsForEcommerce(
        order.basketProducts.map(mapBasketProduct), 'item_id'
      )
    }
  } : {
    event: 'purchase',
    ecommerce: {
      purchase: {
        actionField: {
          id: order.displayId,
          affiliation: order.vendorName,
          revenue: order.subTotal,
          tax: order.vendorTax,
          shipping: order.deliveryCharge,
          coupon: orderSubmission.basket.coupon ? orderSubmission.basket.coupon.code : ''
        },
        products: combineMatchingBasketProductsForEcommerce(
          order.basketProducts.map(mapBasketProductUA)
        )
      }
    }
  };
}

function mapClickProductToDataLayer(product, clickFrom) {
  return isGa4() ? {
    event: 'select_item',
    ecommerce: {
      items: [mapProduct(product)]
    }
  } : {
    event: 'productClick',
    ecommerce: {
      click: {
        actionField: {list: clickFrom},
        products: [mapProductUA(product)]
      }
    }
  };
}

function mapViewProductDetailToDataLayer(product, viewIn) {
  return isGa4() ? {
    event: 'view_item',
    ecommerce: {
      items: [mapProduct(product)]
    }
  } : {
    event: 'detail',
    ecommerce: {
      detail: {
        actionField: {list: viewIn},
        products: [mapProductUA(product)]
      }
    }
  };
}

function mapProductsVisibleToDataLayer(products) {
  return isGa4() ? {
    event: 'view_item_list',
    ecommerce: {
      items: products.map(mapProduct)
    }
  } : {
    event: 'impressions',
    ecommerce: {
      currencyCode: getVendor().currency,
      impressions: products.map(mapProductUA)
    }
  };
}

function mapUserLoginToDataLayer(user) {
  let method = 'olo';
  if (user.isSsoLogin) method = 'third-party';
  if (user.isFacebook) method = 'facebook';
  return {
    event: 'login',
    method: method
  };
}



/**
 * EVENT LISTENERS
 */
oloOn('v0.transaction', function(order, orderSubmission) {
  dataLayerPush({
    event: 'trackTrans',
    transactionId: order.id,
    transactionAffiliation: order.vendorName,
    transactionTotal: roundTo(order.vendorTotal || 0, 2),
    transactionTax: roundTo(order.vendorTax || 0, 2),
    transactionShipping: order.deliveryCharge,
    transactionZipCode: order.vendor.address ? order.vendor.address.postalCode : undefined,
    transactionStoreNumber: order.vendor.id,
    transactionStoreReference: order.vendor.externalReference,
    transactionProducts: combineMatchingBasketProductsForEcommerce(order.basketProducts.map(mapBasketProductLegacy), 'sku'),
    transactionHandoff: order.handoffDescription,
    transactionRevenue: roundTo(order.subTotal || 0, 2),
    transactionCurrency: getVendor().currency
  });
});

oloOn('v1.addToCart', function(product) {
  dataLayerPush(mapAddToCartToDataLayer(product));
});

oloOn('v1.removeFromCart', function(product) {
  dataLayerPush(mapRemoveFromCartToDataLayer(product));
});

oloOn('v1.checkout', function(basket, cb) {
  dataLayerPush(mapCheckoutToDataLayer(basket, cb));
});

oloOn('v1.transaction', function(order, orderSubmission) {
  dataLayerPush(mapTransactionToDataLayer(order, orderSubmission));
});

oloOn('v1.clickProductLink', function(product, clickFrom) {
  dataLayerPush(mapClickProductToDataLayer(product, clickFrom));
});

oloOn('v1.viewProductDetail', function(product, viewIn) {
  dataLayerPush(mapViewProductDetailToDataLayer(product, viewIn));
});

oloOn('v1.productsVisible', function(products) {
  dataLayerPush(mapProductsVisibleToDataLayer(products));
});

oloOn('v1.userLogin', function(user) {
  dataLayerPush(mapUserLoginToDataLayer(user));
});





/**
 * TAG IS FINISHED SETTING UP
 */
data.gtmOnSuccess();


